import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { MdPlaylistAdd } from 'react-icons/md';
import { SongContext } from '../contexts/SongContext';

const NewSong = () => {
  const { addSong } = useContext(SongContext);
  const [title, setTitle] = useState('');
  const [artist, setArtist] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    addSong(title, artist);
    setTitle('');
    setArtist('');
  };

  return (
    <ModalWrapper className='modal-content'>
      <div className='add'>
        <a href='#add-a-song' rel='lightbox'>
          <MdPlaylistAdd className='add-button' title='add a song' />
        </a>
      </div>
      <div className='lightbox' id='add-a-song'>
        <p>
          Cum sociis natoque penatibus et magnis dis parturient montes, nascetur
          ridiculus mus. Morbi leo risus, porta ac consectetur ac, vestibulum at
          eros. Integer posuere erat a ante venenatis dapibus posuere velit
          aliquet. Cum sociis natoque penatibus et magnis dis parturient montes,
          nascetur ridiculus mus. Morbi leo risus, porta ac consectetur ac,
          vestibulum at eros. Integer posuere erat a ante venenatis dapibus
          posuere velit aliquet. Cum sociis natoque penatibus et magnis dis
          parturient montes, nascetur ridiculus mus. Morbi leo risus, porta ac
          consectetur ac, vestibulum at eros. Integer posuere erat a ante
          venenatis dapibus posuere velit aliquet. Cum sociis natoque penatibus
          et magnis dis parturient montes, nascetur ridiculus mus. Morbi leo
          risus, porta ac consectetur ac, vestibulum at eros.
        </p>
        <br />

        <a className='close' href='#'>
          &times;
        </a>
      </div>
    </ModalWrapper>
  );
};
const ModalWrapper = styled.div`
  .modal-content {
  }

  a {
    color: #333;
  }

  .lightbox {
    background: #fff;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.25);
    border-radius: 12px;
    opacity: 0;
    padding: 30px;
    left: 50%;
    transform: translateX(-50%);
    top: 32px;
    visibility: hidden;
    width: 65%;
    position: absolute;
    transition: all 0.3s ease-in;
    z-index: 1;
    min-height: 65vh;
  }

  .lightbox .close {
    background: #fff;
    color: #000;
    display: block;
    font-weight: bold;
    height: 3rem;
    line-height: 3rem;
    text-align: center;
    text-decoration: none;
    width: 3rem;
    border-radius: 100%;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.5);
    position: absolute;
    top: -1.5rem;
    right: -1.5rem;
  }

  .lightbox:target {
    opacity: 1;
    visibility: visible;
  }
  .add-button {
    font-size: 32px;
    color: #fff;
  }
`;
export default NewSong;
