import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { MdPlaylistAdd } from 'react-icons/md';
import { SongContext } from '../contexts/SongContext';

const NewSong = () => {
  const { addSong } = useContext(SongContext);
  const [title, setTitle] = useState('');
  const [artist, setArtist] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    addSong(title, artist);
    setTitle('');
    setArtist('');
  };

  return (
    <ModalWrapper className='modal-content'>
      <div className='add'>
        <a href='#add-a-song' rel='lightbox'>
          <MdPlaylistAdd className='add-button' title='add a song' />
        </a>
      </div>
      <div className='lightbox' id='add-a-song'>
        <h2>Add song in the list</h2>
        <form onSubmit={handleSubmit}>
          <input
            type='text'
            name='title'
            value={title}
            onChange={e => setTitle(e.target.value)}
            placeholder='add  title...'
          />
          <input
            type='text'
            name='artist'
            value={artist}
            onChange={e => setArtist(e.target.value)}
            placeholder='add  artist...'
          />
          <input type='submit' className='add-button' value='Add a Song' />
        </form>

        <a className='close' href='#'>
          &times;
        </a>
      </div>
    </ModalWrapper>
  );
};
const ModalWrapper = styled.div`
  .modal-content {
  }

  a {
    color: #333;
  }

  .lightbox {
    background: #fff;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.25);
    border-radius: 12px;
    opacity: 0;
    padding: 30px;
    left: 50%;
    transform: translateX(-50%);
    top: 125px;
    visibility: hidden;
    width: 50%;
    position: absolute;
    transition: all 0.3s ease-in;
    z-index: 1;
    min-height: 45vh;
  }

  .lightbox .close {
    background: #fff;
    color: #000;
    display: block;
    font-weight: bold;
    height: 3rem;
    line-height: 3rem;
    text-align: center;
    text-decoration: none;
    width: 3rem;
    border-radius: 100%;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.5);
    position: absolute;
    top: -1.5rem;
    right: -1.5rem;
  }

  .lightbox:target {
    opacity: 1;
    visibility: visible;
  }
  .add-button {
    font-size: 32px;
    color: #fff;
  }

  form {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  form input {
    width: 65%;
    margin: 10px 0;
    border-radius: 5px;
    padding: 20px;
    padding-left: 10px;
    border: none;
    height: 48px;
  }

  form .add-button {
    height: 48px;
  }

  form h2 {
    margin-bottom: 50px;
  }
`;
export default NewSong;
