import React from 'react';
import styled from 'styled-components';

const Modal = () => {
  return (
    <ModalWrapper>
      {/* <!-- partial:index.partial.html --> */}
      <a href='#lightbox' rel='lightbox'>
        Open modal
      </a>
      <div class='lightbox' id='lightbox'>
        <p>
          Cum sociis natoque penatibus et magnis dis parturient montes, nascetur
          ridiculus mus. Morbi leo risus, porta ac consectetur ac, vestibulum at
          eros. Integer posuere erat a ante venenatis dapibus posuere velit
          aliquet.
        </p>
        <a class='close' href='#'>
          &times;
        </a>
      </div>
    </ModalWrapper>
  );
};
const ModalWrapper = styled.div`
  a {
    color: #333;
  }

  .lightbox {
    background: #fff;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.25);
    border-radius: 12px;
    opacity: 0;
    padding: 20px;
    visibility: hidden;
    width: 50%;
    position: absolute;
    top: 2rem;
    left: 50%;
    margin-left: -25%;
    transition: all 0.25s ease;
    z-index: -1;
  }

  .lightbox .close {
    background: #fff;
    color: #000;
    display: block;
    font-weight: bold;
    height: 3rem;
    line-height: 3rem;
    text-align: center;
    text-decoration: none;
    width: 3rem;
    border-radius: 100%;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.5);
    position: absolute;
    top: -1.5rem;
    right: -1.5rem;
  }

  .lightbox:target {
    opacity: 1;
    visibility: visible;
  }
`;
export default Modal;
