import React, { createContext, useState, useEffect } from 'react';
import uuid from 'uuid/v4';
export const SongContext = createContext();

const SongContextProvider = props => {
  const [songs, setSongs] = useState(() => {
    const localData = localStorage.getItem('songs');
    return localData
      ? JSON.parse(localData)
      : [
          {
            id: uuid(),
            title: 'Bad',
            artist: 'Michael Jackson',
            url: 'https://www.youtube.com/watch?v=dsUXAEzaC3Q',
            like: true
          },
          {
            id: uuid(),
            title: 'Dumb',
            artist: ' Nirvana',
            url: 'https://www.youtube.com/watch?v=4xRuxuHjBY4',
            like: false
          },
          {
            id: uuid(),
            title: 'Aquarela',
            artist: 'Toquinho',
            url: 'https://www.youtube.com/watch?v=4xRuxuHjBY4',
            like: true
          },
          {
            id: uuid(),
            title: 'Paranoid',
            artist: 'Black Sabbath',
            like: false,
            url: 'https://www.youtube.com/watch?v=0qanF-91aJo'
          }
        ];
  });
  useEffect(() => {
    localStorage.setItem('songs', JSON.stringify(songs));
  }, [songs]);

  //toggleLike
  const toggleLike = songId => {
    const updatedSongs = songs.map(song =>
      song.id === songId ? { ...song, like: !song.like } : song
    );
    setSongs(updatedSongs);
  };
  //removeSong
  const removeSong = id => {
    setSongs(songs.filter(song => song.id !== id));
  };

  //addSong
  const addSong = (title, artist, url) => {
    setSongs([...songs, { title, artist, id: uuid(), like: false }]);
  };

  return (
    <SongContext.Provider value={{ songs, toggleLike, removeSong, addSong }}>
      {props.children}
    </SongContext.Provider>
  );
};

export default SongContextProvider;
